mapper.select:
select * from product_type 


		id=#{productTypeId}


ORDER BY sort_num

select * from product_type 


		code != #{excludeProductTypeCode}


ORDER BY sort_num

SELECT DISTINCT(pt.`code`),pt.`name`,pt.`id` FROM investor_product_config AS upc LEFT JOIN product ON product.short_code = upc.product_code JOIN product_type pt ON pt.id = product.product_type_id
upc.investor_account_id = #{investorId}
and pt.`code` != #{excludeProductTypeCode}
ORDER BY product.sort_num
