mapper.select:
select * from fund_io_cash_recharge


		and user_id=#{userId}


		and third_optcode=#{thirdOptcode}


		and status=#{status}


		and transfer_number=#{transferNumber}


ORDER BY create_date desc

select * from fund_io_cash_recharge


		and third_optcode=#{thirdOptcode}


		and status=#{status}


		and create_date >#{createDate}


order by
	create_date asc

		limit #{queryNum}
