mapper.select:
select coi.* from v_order_score_info coi


		and coi.last_buy_date >= concat(DATE_FORMAT(NOW(),'%Y-%m-%d'),' 00:00:00') 
		and concat(DATE_FORMAT(NOW(),'%Y-%m-%d'),' 23:59:59') >= coi.last_buy_date


		and coi.id=#{id}


		and coi.order_id=#{orderId}


		and coi.user_id=#{userId}


		and coi.nick_name=#{nickName}


		and coi.tele=#{tele}


		and coi.short_code=#{shortCode}


		and coi.product_code=#{productCode}


		and coi.trade_direction=#{tradeDirection}


		and coi.status=#{status}


		and coi.sell_trigger_type=#{sellTriggerType}


		and coi.defer_status=#{deferStatus}


		and coi.trail_stop_loss=#{trailStopLoss}


		and coi.account_id=#{accountId}


		and coi.investor_id=#{investorId}


		and coi.buy_avg_price >= #{minBuyPrice}


		and #{maxBuyPrice} >= coi.buy_avg_price


		and coi.sell_avg_price >= #{minSellPrice}


		and #{maxSellPrice} >= coi.sell_avg_price


		and coi.actual_hold_fund >= #{minHoldFund}


		and #{maxHoldFund} >= coi.actual_hold_fund


		and coi.actual_counter_fee >= #{minCounterFee}


		and #{maxCounterFee} >= coi.actual_counter_fee


		and coi.entrust_buy_date >= #{beginBuyDate}


		and #{endBuyDate} >= coi.entrust_buy_date


		and coi.entrust_sell_date >= #{beginSellDate}


		and #{endSellDate} >= coi.entrust_sell_date


		and loss_profit >= #{minLossProfit}


		and #{maxLossProfit} >= loss_profit


		and actual_defer_fund >= #{minActualDeferFund}


		and #{maxActualDeferFund} >= actual_defer_fund


		and defer_interest >= #{minDeferInterest}


		and #{maxDeferInterest} >= defer_interest


		and coi.brand_id = #{brandId}


		and coi.product_type_id = #{productTypeId}


ORDER BY coi.id desc

	limit ${offset},${limit}

select count(coi.id) from v_order_score_info coi


		and coi.last_buy_date >= concat(DATE_FORMAT(NOW(),'%Y-%m-%d'),' 00:00:00') 
		and concat(DATE_FORMAT(NOW(),'%Y-%m-%d'),' 23:59:59') >= coi.last_buy_date


		and coi.id=#{id}


		and coi.order_id=#{orderId}


		and coi.user_id=#{userId}


		and coi.nick_name=#{nickName}


		and coi.tele=#{tele}


		and coi.short_code=#{shortCode}


		and coi.product_code=#{productCode}


		and coi.trade_direction=#{tradeDirection}


		and coi.status=#{status}


		and coi.sell_trigger_type=#{sellTriggerType}


		and coi.defer_status=#{deferStatus}


		and coi.trail_stop_loss=#{trailStopLoss}


		and coi.account_id=#{accountId}


		and coi.investor_id=#{investorId}


		and coi.buy_avg_price >= #{minBuyPrice}


		and #{maxBuyPrice} >= coi.buy_avg_price


		and coi.sell_avg_price >= #{minSellPrice}


		and #{maxSellPrice} >= coi.sell_avg_price


		and coi.actual_hold_fund >= #{minHoldFund}


		and #{maxHoldFund} >= coi.actual_hold_fund


		and coi.actual_counter_fee >= #{minCounterFee}


		and #{maxCounterFee} >= coi.actual_counter_fee


		and coi.entrust_buy_date >= #{beginBuyDate}


		and #{endBuyDate} >= coi.entrust_buy_date


		and coi.entrust_sell_date >= #{beginSellDate}


		and #{endSellDate} >= coi.entrust_sell_date


		and loss_profit >= #{minLossProfit}


		and #{maxLossProfit} >= loss_profit


		and actual_defer_fund >= #{minActualDeferFund}


		and #{maxActualDeferFund} >= actual_defer_fund


		and defer_interest >= #{minDeferInterest}


		and #{maxDeferInterest} >= defer_interest


		and coi.brand_id = #{brandId}


		and coi.product_type_id = #{productTypeId}

		select sum(coi.buy_success_count) orderCount,sum(case when coi.trade_direction=1 then coi.buy_success_count else 0 end) moreCount,
 		sum(case when coi.trade_direction=2 then coi.buy_success_count else 0 end) lessCount,
		sum(case when coi.`status`=3 then coi.hold_count else 0 end) holdCount,
		sum(coi.actual_counter_fee) actualCounterFee,sum(ifnull(coi.loss_profit,0)) lossProfit,
		sum(coi.defer_interest) deferInterest,COUNT(DISTINCT coi.user_id) userCount,
		sum(case when coi.trade_direction=1 and coi.status=3 then coi.buy_success_count else 0 end) longPosition,
		sum(case when coi.trade_direction=2 and coi.status=3 then coi.buy_success_count else 0 end) shortPosition,
		ABS(sum(case when coi.trade_direction=1 and coi.status=3 then coi.buy_success_count else 0 end)-
		sum(case when coi.trade_direction=2 and coi.status=3 then coi.buy_success_count else 0 end)) netPosition
 		from v_order_score_info coi


				and coi.last_buy_date >= concat(DATE_FORMAT(NOW(),'%Y-%m-%d'),' 00:00:00') 
				and concat(DATE_FORMAT(NOW(),'%Y-%m-%d'),' 23:59:59') >= coi.last_buy_date


				and coi.id=#{id}


				and coi.order_id=#{orderId}


				and coi.user_id=#{userId}


				and coi.nick_name=#{nickName}


				and coi.tele=#{tele}


				and coi.short_code=#{shortCode}


				and coi.product_code=#{productCode}


				and coi.trade_direction=#{tradeDirection}


				and coi.status=#{status}


				and coi.sell_trigger_type=#{sellTriggerType}


				and coi.defer_status=#{deferStatus}


				and coi.trail_stop_loss=#{trailStopLoss}


				and coi.investor_id=#{investorId}


				and coi.account_id=#{accountId}


				and coi.buy_avg_price >= #{minBuyPrice}


				and #{maxBuyPrice} >= coi.buy_avg_price


				and coi.sell_avg_price >= #{minSellPrice}


				and #{maxSellPrice} >= coi.sell_avg_price


				and coi.actual_hold_fund >= #{minHoldFund}


				and #{maxHoldFund} >= coi.actual_hold_fund


				and coi.actual_counter_fee >= #{minCounterFee}


				and #{maxCounterFee} >= coi.actual_counter_fee


				and coi.entrust_buy_date >= #{beginBuyDate}


				and #{endBuyDate} >= coi.entrust_buy_date


				and coi.entrust_sell_date >= #{beginSellDate}


				and #{endSellDate} >= coi.entrust_sell_date


				and coi.brand_id = #{brandId}


				and coi.product_type_id = #{productTypeId}

select DISTINCT coi.id, coi.user_commit_buy_date date,'用户提交买入' commitType ,(case when coi.buy_entrust_type = 1 then '市价' else '限价' end) priceType ,coi.user_commit_buy_price price from order_score_info coi where coi.order_id=#{id}
UNION ALL
select DISTINCT coi.id,coi.entrust_buy_date date,'系统提交买入' commitType ,(case when coer.entrust_type = 1 then '市价' else '限价' end) priceType ,coi.entrust_buy_price price from order_score_info coi join order_score_entrust_info coer on(coi.order_id=coer.order_id) where coer.trade_type=1 and coi.order_id=#{id}
UNION ALL
select DISTINCT coi.id,max(cotr.success_date) date,'系统开仓完成' commitType ,(case when coer.entrust_type = 1 then '市价' else '限价' end) priceType ,cotr.success_price price from order_score_info coi join order_score_success_info cotr on(coi.order_id=cotr.order_id) JOIN order_score_entrust_info coer on(coi.order_id=coer.order_id) where cotr.trade_type = 1 and coi.order_id=#{id}
UNION ALL
select DISTINCT coi.id,coi.user_commit_sell_date date,'用户提交卖出' commitType ,(case when coi.sell_entrust_type = 1 then '市价' else '限价' end) priceType ,coi.user_commit_sell_price price from order_score_info coi where coi.order_id=#{id}
UNION ALL
select DISTINCT coi.id,coi.entrust_sell_date date,'系统提交卖出' commitType ,(case when coer.entrust_type = 1 then '市价' else '限价' end) priceType ,coi.entrust_sell_price price from order_score_info coi join order_score_entrust_info coer on(coi.order_id=coer.order_id) where coer.trade_type=2 and coi.order_id=#{id}
UNION ALL
select DISTINCT coi.id,max(cotr.success_date) date,'系统平仓完成' commitType ,(case when coer.entrust_type = 1 then '市价' else '限价' end) priceType ,cotr.success_price price from order_score_info coi join order_score_success_info cotr on(coi.order_id=cotr.order_id) JOIN order_score_entrust_info coer on(coi.order_id=coer.order_id) where cotr.trade_type = 2 and coi.order_id=#{id}

mapper.update:
update order_score_info
condition_id=#{conditionId}, user_id=#{userId}, nick_name=#{nickName}, investor_id=#{investorId}, account_id=#{accountId}, security_code=#{securityCode}, per_stop_profit=#{perStopProfit}, per_stop_loss=#{perStopLoss}, per_counter_fee=#{perCounterFee}, per_surcharge=#{perSurcharge}, per_defer_fund=#{perDeferFund}, per_defer_interest=#{perDeferInterest}, product_id=#{productId}, product_code=#{productCode}, product_name=#{productName}, currency=#{currency}, unit=#{unit}, rate=#{rate}, plate=#{plate}, exchange_code=#{exchangeCode}, display=#{display}, defer_status=#{deferStatus}, trail_stop_loss=#{trailStopLoss}, balance_status=#{balanceStatus}, buy_trigger_type=#{buyTriggerType}, buy_entrust_type=#{buyEntrustType}, user_commit_buy_date=#{userCommitBuyDate}, entrust_buy_date=#{entrustBuyDate}, last_buy_date=#{lastBuyDate}, user_commit_buy_price=#{userCommitBuyPrice}, entrust_buy_price=#{entrustBuyPrice}, buy_avg_price=#{buyAvgPrice}, sell_trigger_type=#{sellTriggerType}, sell_entrust_type=#{sellEntrustType}, user_commit_sell_date=#{userCommitSellDate}, entrust_sell_date=#{entrustSellDate}, last_sell_date=#{lastSellDate}, user_commit_sell_price=#{userCommitSellPrice}, entrust_sell_price=#{entrustSellPrice}, sell_avg_price=#{sellAvgPrice}, trade_direction=#{tradeDirection}, sys_set_sell_date=#{sysSetSellDate}, buy_entrust_count=#{buyEntrustCount}, buy_success_count=#{buySuccessCount}, buy_fail_count=#{buyFailCount}, hold_count=#{holdCount}, sell_entrust_count=#{sellEntrustCount}, sell_success_count=#{sellSuccessCount}, stop_loss_price=#{stopLossPrice}, stop_loss=#{stopLoss}, stop_profit_price=#{stopProfitPrice}, stop_profit=#{stopProfit}, sys_loss_profit=#{sysLossProfit}, loss_profit=#{lossProfit}, defer_interest=#{deferInterest}, defer_times=#{deferTimes}, should_hold_fund=#{shouldHoldFund}, should_defer_fund=#{shouldDeferFund}, should_counter_fee=#{shouldCounterFee}, should_coupon_fund=#{shouldCouponFund}, actual_hold_fund=#{actualHoldFund}, actual_defer_fund=#{actualDeferFund}, actual_counter_fee=#{actualCounterFee}, actual_coupon_fund=#{actualCouponFund}, create_date=#{createDate}, modify_date= now()
where order_id = #{orderId}
