where order_id = #{orderId}

mapper.select:
select * from order_cash_info v left join product on v.product_code = product.product_code where v.user_id = #{userId} and v.hold_count > 0 and (v.sell_entrust_count=0 or v.sell_entrust_count is null) and product.status = 1 and product.market_status in (2,3)
and v.product_code = #{productCode}
order by v.id desc

select coi.user_id as userId, coi.id as orderId, coi.buy_avg_price, coi.trade_direction , coi.product_code as productCode from order_cash_info as coi where 1 = 1 and coi.hold_count > 0 and (coi.sell_entrust_count=0 or coi.sell_entrust_count is null)
and coi.user_id = #{userId}
and coi.order_id=#{orderId}


  SELECT * from order_cash_entrust_info 
 left join order_cash_info on order_cash_entrust_info.order_id = order_cash_info.order_id
	 where order_cash_info.user_id = #{userId}
  order BY order_cash_entrust_info.id desc
	 LIMIT #{limit1}, #{limit2}

mapper.update:
update order_cash_info

	sys_set_sell_date=#{sysSetSellDate},
	modify_date= now()

where order_id = #{orderId}
and defer_status = 0;
