mapper.select:
select product.*,pt.`name` AS typeName,ex.plate AS plate ,pcs.sort AS sort from product LEFT JOIN product_sort pcs on product.short_code = pcs.short_code JOIN product_type pt on pt.id = product.product_type_id JOIN user_product_select as ps on product.short_code = ps.product_short_code JOIN exchange_info as ex on product.exchange_id=ex.id where ps.user_id = #{userId} and NOW() > product.expiration_begin_time and product.expiration_time >= NOW() and product.status in (1,2)
and pt.code!=#{excludeProductTypeCode}
ORDER BY ps.create_date desc;

	select pt.`name` ,product.*,ex.plate,pcs.sort AS sort from investor_product_config as upc
	left JOIN product on product.short_code = upc.product_code
	left JOIN product_sort pcs on product.short_code = pcs.short_code
    JOIN product_type pt on pt.id = product.product_type_id
    JOIN user_product_select as ps on product.short_code = ps.product_short_code
	JOIN exchange_info as ex on product.exchange_id=ex.id
	where NOW() > product.expiration_begin_time and product.expiration_time >= NOW()
	and product.status in (1,2)
	and ps.user_id = #{userId}
	and upc.investor_account_id = #{investorId}

		and pt.code!=#{excludeProductTypeCode}

	ORDER BY ps.create_date desc ;
