ON  t1.channel_id = t2.channel_id where
bank_code = #{bankCode}

	and t1.channel_id in (

		#{channel}				

	)

select * from bank_charge_detail where user_id = #{userId} and bank_card_num = #{bankcardCode} 
and create_time = #{createTime} 

	and channel_id in (

